import * as React from 'react';
import {useCallback, useState} from 'react';
import Map, {Popup} from 'react-map-gl';
import 'mapbox-gl/dist/mapbox-gl.css';
import Typography from "@mui/material/Typography";

const MAPBOX_TOKEN = 'pk.eyJ1Ijoic2VhbmF0aGFubWxlZSIsImEiOiJjbGZvMjhlb3AwOWVlNDhtdGZ5ZHM2cGVmIn0.5cMO3LeHwk_6_ekhcIT-rg';

export default function MapControls() {
    const [popupInfo, setPopupInfo] = useState(null);

    const onClick = useCallback(evt => {
        const feature = evt.features[0]
        if (feature)
        {  
            const info = {longitude: evt.lngLat.lng, latitude: evt.lngLat.lat, title: feature.properties.title, description: feature.properties.description, address: feature.properties.address, image: feature.properties.image}
            setPopupInfo(info);
        }
    }, []);

    return (
        <Map
            initialViewState={{
                longitude: 2.272,
                latitude: 48.840,
                zoom: 11.42,
                bearing: 0,
                pitch: 0
            }}
            style={{position: 'absolute', width: '100vw', height: '100vh'}}
            mapStyle="mapbox://styles/seanathanmlee/clfo64h7x000701lapnvwqt1l"
            mapboxAccessToken={MAPBOX_TOKEN}
            interactiveLayerIds={["bride-paris-destinations"]}
            onClick={onClick}
        >
            {popupInfo && (
                <Popup
                    longitude={popupInfo.longitude}
                    latitude={popupInfo.latitude}
                    anchor="top"
                    onClose={() => setPopupInfo(null)}>
                        <Typography variant="body1" marked="center">
                            {popupInfo.title}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                            {popupInfo.address}
                        </Typography>
                        <img src={popupInfo.image} width="100%" height="auto"/>
                        <Typography variant="body2" color="text.secondary">
                            {popupInfo.description}
                        </Typography>
                </Popup>)}
        </Map>
    );
}
